import java.io.* ; import java.util.* ; import java.util.stream.* ; public class atcoder_ABC112_A { private static BufferedReader br = null ; public static void main ( String [ ] args ) { int n = Integer.parseInt ( read ( 1 ) [ 0 ] ) ; if ( n == 1 ) { System.out.println ( "Hello World" ) ; } else { String [ ] strs = read ( 2 ) ; System.out.println ( Integer.parseInt ( strs [ 0 ] ) + Integer.parseInt ( strs [ 1 ] ) ) ; } } private static String [ ] read ( int len ) { List < String > strList = new ArrayList < > ( ) ; try { if ( br == null ) { br = new BufferedReader ( new InputStreamReader ( System.in ) ) ; } for ( int i = 0 ; i < len ; i ++ ) { strList.add ( br.readLine ( ) ) ; } } catch ( IOException e ) { } return strList.toArray ( new String [ 0 ] ) ; } private static void close ( ) { try { if ( br != null ) { br.close ( ) ; } } catch ( IOException e ) { } } }
