import java.util.* ; public class atcoder_ABC126_E { Scanner sc = new Scanner ( System.in ) ; final int MOD = 1000000007 ; void doIt ( ) { int n = sc.nextInt ( ) ; UnionFindTree utf = new UnionFindTree ( n ) ; int m = sc.nextInt ( ) ; for ( int i = 0 ; i < m ; i ++ ) { int x = sc.nextInt ( ) - 1 ; int y = sc.nextInt ( ) - 1 ; int z = sc.nextInt ( ) ; utf.unite ( x , y ) ; } int cnt = 0 ; for ( int i = 0 ; i < n ; i ++ ) { if ( utf.par [ i ] == i ) { cnt ++ ; } } System.out.println ( cnt ) ; } class UnionFindTree { int n ; int [ ] par ; int [ ] rank ; UnionFindTree ( int n ) { this.n = n ; par = new int [ n ] ; rank = new int [ n ] ; for ( int i = 0 ; i < n ; i ++ ) { par [ i ] = i ; rank [ i ] = 0 ; } } int find ( int x ) { if ( par [ x ] == x ) { return x ; } else { return par [ x ] = find ( par [ x ] ) ; } } void unite ( int x , int y ) { x = find ( x ) ; y = find ( y ) ; if ( x == y ) return ; if ( rank [ x ] < rank [ y ] ) par [ x ] = y ; else { par [ y ] = x ; if ( rank [ x ] == rank [ y ] ) rank [ x ] ++ ; } } boolean same ( int x , int y ) { return find ( x ) == find ( y ) ; } } public static void main ( String [ ] args ) { new atcoder_ABC126_E ( ).doIt ( ) ; } }
