import java.util.* ; public class atcoder_ABC134_E { public static void main ( String [ ] args ) throws Exception { Scanner sc = new Scanner ( System.in ) ; int n = sc.nextInt ( ) ; TreeMap < Integer , Integer > map = new TreeMap < Integer , Integer > ( ) ; while ( n -- > 0 ) { int num = sc.nextInt ( ) ; if ( map.lowerKey ( num ) == null ) { int val = map.getOrDefault ( num , 0 ) ; map.put ( num , ++ val ) ; } else { int key = map.lowerKey ( num ) ; int val = map.get ( key ) ; if ( val == 1 ) { map.remove ( key ) ; } else { map.put ( key , -- val ) ; } val = map.getOrDefault ( num , 0 ) ; map.put ( num , ++ val ) ; } } int ans = 0 ; for ( Integer key : map.keySet ( ) ) { int val = map.get ( key ) ; ans += val ; } System.out.println ( ans ) ; } }
