import java.util.* ; public class atcoder_ABC166_C { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int N = sc.nextInt ( ) ; long M = sc.nextLong ( ) ; long [ ] H = new long [ N ] ; boolean [ ] ans = new boolean [ N ] ; for ( int i = 0 ; i < N ; i ++ ) { H [ i ] = sc.nextLong ( ) ; ans [ i ] = true ; } for ( int i = 0 ; i < M ; i ++ ) { int temp1 = sc.nextInt ( ) ; int temp2 = sc.nextInt ( ) ; if ( H [ temp1 - 1 ] < H [ temp2 - 1 ] ) { ans [ temp1 - 1 ] = false ; } else if ( H [ temp1 - 1 ] > H [ temp2 - 1 ] ) { ans [ temp2 - 1 ] = false ; } else { ans [ temp1 - 1 ] = false ; ans [ temp2 - 1 ] = false ; } } int ans2 = 0 ; for ( int i = 0 ; i < N ; i ++ ) { if ( ans [ i ] ) { ans2 ++ ; } } System.out.println ( ans2 ) ; } }
