import java.util.* ; public class atcoder_AGC013_A { public static void main ( String [ ] args ) { Scanner stdIn = new Scanner ( System.in ) ; int N = stdIn.nextInt ( ) ; int [ ] A = new int [ N ] ; for ( int i = 0 ; i < N ; i ++ ) { A [ i ] = stdIn.nextInt ( ) ; } int count = 0 ; for ( int i = 0 ; i < N ; i ++ ) { if ( i == N - 1 ) count ++ ; else if ( A [ i ] == A [ i + 1 ] ) ; else if ( A [ i ] < A [ i + 1 ] ) { while ( A [ i ] <= A [ i + 1 ] ) { i ++ ; if ( i == N - 1 ) break ; } count ++ ; } else { while ( A [ i ] >= A [ i + 1 ] ) { i ++ ; if ( i == N - 1 ) break ; } count ++ ; } } System.out.println ( count ) ; } }
