import java.util.ArrayList ; import java.util.HashMap ; import java.util.List ; import java.util.Map ; import java.util.Scanner ; public class atcoder_AGC024_B { public static void main ( String [ ] args ) { Scanner scan = new Scanner ( System.in ) ; int N = scan.nextInt ( ) ; int [ ] P = new int [ N ] ; for ( int i = 0 ; i < N ; ++ i ) P [ i ] = scan.nextInt ( ) - 1 ; Map < Integer , Integer > map = new HashMap < > ( ) ; for ( int i = 0 ; i < N ; ++ i ) map.put ( P [ i ] , i ) ; List < Integer > nglist = new ArrayList < > ( ) ; for ( int i = 0 ; i < N - 1 ; ++ i ) if ( map.get ( i ) > map.get ( i + 1 ) ) nglist.add ( i ) ; if ( nglist.isEmpty ( ) ) { System.out.println ( 0 ) ; return ; } int ans = Math.min ( N - nglist.get ( 0 ) - 1 , nglist.get ( nglist.size ( ) - 1 ) + 1 ) ; for ( int i = 0 ; i < nglist.size ( ) - 1 ; ++ i ) { ans = Math.min ( nglist.get ( i ) + 1 + N - nglist.get ( i + 1 ) - 1 , ans ) ; } System.out.println ( ans ) ; } }
