import java.io.BufferedReader ; import java.io.InputStreamReader ; public class atcoder_AGC028_B { public static void main ( String [ ] args ) throws Exception { BufferedReader br = new BufferedReader ( new InputStreamReader ( System.in ) ) ; int n = Integer.parseInt ( br.readLine ( ) ) ; String [ ] sa = br.readLine ( ).split ( " " ) ; int [ ] a = new int [ n ] ; for ( int i = 0 ; i < n ; i ++ ) { a [ i ] = Integer.parseInt ( sa [ i ] ) ; } br.close ( ) ; int mod = 1000000007 ; long p = 1 ; for ( int i = 2 ; i <= n ; i ++ ) { p = p * i % mod ; } long [ ] q = new long [ n ] ; q [ 0 ] = 1 ; for ( int i = 1 ; i < n ; i ++ ) { q [ i ] = ( q [ i - 1 ] + modinv ( i + 1 , mod ) ) % mod ; } long ans = 0 ; for ( int i = 0 ; i < n ; i ++ ) { long val = q [ i ] + q [ n - i - 1 ] - 1 ; val *= a [ i ] ; val %= mod ; val *= p ; val %= mod ; ans += val ; ans %= mod ; } System.out.println ( ans ) ; } static long modinv ( long a , int m ) { long b = m ; long u = 1 ; long v = 0 ; long tmp = 0 ; while ( b > 0 ) { long t = a / b ; a -= t * b ; tmp = a ; a = b ; b = tmp ; u -= t * v ; tmp = u ; u = v ; v = tmp ; } u %= m ; if ( u < 0 ) u += m ; return u ; } }
