import java.util.* ; import java.lang.* ; public class atcoder_AGC033_B { public static void main ( String [ ] args ) { Scanner scanner = new Scanner ( System.in ) ; int h = scanner.nextInt ( ) ; int w = scanner.nextInt ( ) ; int n = scanner.nextInt ( ) ; int sr = scanner.nextInt ( ) ; int sc = scanner.nextInt ( ) ; String s = scanner.next ( ) ; String t = scanner.next ( ) ; boolean end = false ; int usafe = 1 ; int dsafe = h ; for ( int i = n - 1 ; i >= 0 ; i -- ) { if ( s.charAt ( i ) == 'U' ) { usafe ++ ; } else if ( s.charAt ( i ) == 'D' ) { dsafe -- ; } if ( usafe > dsafe ) { end = true ; break ; } if ( i > 0 ) { if ( t.charAt ( i - 1 ) == 'U' ) { dsafe = Math.min ( dsafe + 1 , h ) ; } else if ( t.charAt ( i - 1 ) == 'D' ) { usafe = Math.max ( usafe - 1 , 1 ) ; } } } int lsafe = 1 ; int rsafe = w ; for ( int i = n - 1 ; i >= 0 ; i -- ) { if ( s.charAt ( i ) == 'L' ) { lsafe ++ ; } else if ( s.charAt ( i ) == 'R' ) { rsafe -- ; } if ( lsafe > rsafe ) { end = true ; break ; } if ( i > 0 ) { if ( t.charAt ( i - 1 ) == 'L' ) { rsafe = Math.min ( rsafe + 1 , w ) ; } else if ( t.charAt ( i - 1 ) == 'R' ) { lsafe = Math.max ( lsafe - 1 , 1 ) ; } } } if ( sr >= usafe && sr <= dsafe && sc >= lsafe && sc <= rsafe && ! end ) { System.out.println ( "YES" ) ; } else { System.out.println ( "NO" ) ; } } }
