import java.util.* ; public class codeforces_189_A { public static void main ( String [ ] args ) { Scanner scanner = new Scanner ( System.in ) ; int n = scanner.nextInt ( ) ; int maxn = 4010 ; int a = scanner.nextInt ( ) ; int b = scanner.nextInt ( ) ; int c = scanner.nextInt ( ) ; int [ ] arr = { a , b , c } ; int [ ] dp = new int [ maxn ] ; for ( int i = 0 ; i < n ; i ++ ) { dp [ i ] = Integer.MIN_VALUE ; } dp [ 0 ] = 0 ; for ( int i = 0 ; i < 3 ; i ++ ) { for ( int j = arr [ i ] ; j < dp.length ; j ++ ) { dp [ j ] = Math.max ( dp [ j ] , dp [ j - arr [ i ] ] + 1 ) ; } } System.out.println ( dp [ n ] ) ; } }
