import java.util.* ; public class codeforces_222_A { public static void main ( String [ ] args ) { Scanner in = new Scanner ( System.in ) ; int n = in.nextInt ( ) , k = in.nextInt ( ) , l = 0 , c = 1 ; for ( int i = 0 ; i < n ; i ++ ) { int a = in.nextInt ( ) ; if ( a == l ) c += 1 ; else c = 1 ; l = a ; } if ( c + k > n ) System.out.println ( n - c ) ; else System.out.println ( - 1 ) ; } }
