import java.io.BufferedReader ; import java.io.IOException ; import java.io.InputStreamReader ; import java.io.PrintWriter ; import java.math.BigInteger ; import java.util.* ; public class codeforces_276_B { public static void main ( String [ ] args ) throws NumberFormatException , IOException { InputStreamReader inputStreamReader = new InputStreamReader ( System.in ) ; BufferedReader bufferedReader = new BufferedReader ( inputStreamReader ) ; PrintWriter writer = new PrintWriter ( System.out ) ; String str = bufferedReader.readLine ( ) ; Map < Character , Integer > map = new HashMap < > ( ) ; int oddCount = 0 ; for ( int i = 0 ; i < str.length ( ) ; i ++ ) { char ch = str.charAt ( i ) ; if ( map.containsKey ( ch ) ) { map.put ( ch , map.get ( ch ) + 1 ) ; } else { map.put ( ch , 1 ) ; } } for ( Map.Entry < Character , Integer > entry : map.entrySet ( ) ) { if ( entry.getValue ( ) % 2 != 0 ) { oddCount ++ ; } } if ( oddCount <= 1 || oddCount % 2 != 0 ) { writer.println ( "First" ) ; } else { writer.println ( "Second" ) ; } writer.flush ( ) ; } }
