import java.io.* ; import java.util.* ; final public class codeforces_351_A { public static void main ( String [ ] args ) throws IOException { BufferedReader br = new BufferedReader ( new InputStreamReader ( System.in ) ) ; StringBuilder sb = new StringBuilder ( ) ; int n = Integer.parseInt ( br.readLine ( ) ) ; StringTokenizer st = new StringTokenizer ( br.readLine ( ) ) ; Double [ ] arr = new Double [ 2 * n ] ; int non_int = 0 ; double sum_before = 0 , sum = 0 ; for ( int i = 0 ; i < 2 * n ; i ++ ) { double num = Double.parseDouble ( st.nextToken ( ) ) ; sum_before += num ; if ( num != Math.floor ( num ) ) non_int ++ ; sum += Math.floor ( num ) ; arr [ i ] = num ; } double max_sum = Math.min ( n , non_int ) + sum ; double min_sum = Math.max ( 0 , non_int - n ) + sum ; double ans ; if ( min_sum > sum_before ) ans = ( min_sum - sum_before ) ; else if ( max_sum < sum_before ) ans = ( sum_before - max_sum ) ; else { double x = sum_before - Math.floor ( sum_before ) ; ans = Math.min ( 1 - x , x ) ; } System.out.printf ( "%.3f" , ans ) ; } }
