import java.io.* ; import java.util.* ; public class codeforces_459_B { public static void main ( String [ ] args ) { Scanner in = new Scanner ( System.in ) ; PrintWriter pw = new PrintWriter ( System.out ) ; int N = in.nextInt ( ) ; int [ ] A = new int [ N ] ; for ( int n = 0 ; n < N ; n ++ ) { A [ n ] = in.nextInt ( ) ; } Arrays.sort ( A ) ; int min = A [ 0 ] ; int max = A [ N - 1 ] ; long mins = 0 ; long maxs = 0 ; for ( int value : A ) { if ( value == min ) mins ++ ; if ( value == max ) maxs ++ ; } if ( min == max ) { pw.println ( ( max - min ) + " " + mins * ( mins - 1 ) / 2 ) ; } else { pw.println ( ( max - min ) + " " + mins * maxs ) ; } pw.close ( ) ; } static void debug ( Object...obj ) { System.err.println ( Arrays.deepToString ( obj ) ) ; } }
