import java.util.* ; import java.io.* ; public class codeforces_474_B { public static void main ( String args [ ] ) throws IOException { Scanner sc = new Scanner ( System.in ) ; int n = sc.nextInt ( ) ; int arr [ ] = new int [ n ] ; arr [ 0 ] = sc.nextInt ( ) ; for ( int i = 1 ; i < n ; i ++ ) { int x = sc.nextInt ( ) ; arr [ i ] = x + arr [ i - 1 ] ; } int m = sc.nextInt ( ) ; int q [ ] = new int [ m ] ; for ( int i = 0 ; i < m ; i ++ ) q [ i ] = sc.nextInt ( ) ; for ( int k = 0 ; k < m ; k ++ ) System.out.println ( fun ( arr , q [ k ] , n , m ) + 1 ) ; } static int fun ( int arr [ ] , int q , int n , int m ) { int res = 0 ; int i = 0 , j = n ; while ( i <= j ) { int md = i + ( j - i ) / 2 ; if ( arr [ md ] == q ) return md ; else if ( arr [ md ] > q ) { res = md ; j = md - 1 ; } else i = md + 1 ; } return res ; } }
