import java.util.* ; import java.io.* ; public class codeforces_519_B { public static void main ( String args [ ] ) throws IOException { Scanner sc = new Scanner ( System.in ) ; int n = sc.nextInt ( ) ; int a = 0 , b = 0 , c = 0 ; for ( int i = 0 ; i < n ; i ++ ) a += sc.nextInt ( ) ; for ( int i = 0 ; i < n - 1 ; i ++ ) b += sc.nextInt ( ) ; for ( int i = 0 ; i < n - 2 ; i ++ ) c += sc.nextInt ( ) ; int x = a - b , y = b - c ; System.out.println ( x ) ; System.out.println ( y ) ; } }
