import java.io.* ; import java.util.* ; public class codeforces_607_A { public static void main ( String [ ] args ) { Scanner sc = new Scanner ( System.in ) ; int max = 0 ; int n = sc.nextInt ( ) ; int [ ] beacon = new int [ 1000001 ] ; int [ ] dp = new int [ 1000001 ] ; for ( int i = 0 ; i < n ; i ++ ) { int a = sc.nextInt ( ) ; beacon [ a ] = sc.nextInt ( ) ; } if ( beacon [ 0 ] != 0 ) dp [ 0 ] = 1 ; for ( int i = 1 ; i < 1000001 ; i ++ ) { if ( beacon [ i ] != 0 && beacon [ i ] < i ) { dp [ i ] = dp [ i - beacon [ i ] - 1 ] + 1 ; } else if ( beacon [ i ] != 0 ) { dp [ i ] = 1 ; } else { dp [ i ] = dp [ i - 1 ] ; } max = Math.max ( max , dp [ i ] ) ; } System.out.print ( n - max ) ; } }
