import java.io.BufferedReader ; import java.io.IOException ; import java.io.InputStreamReader ; public class codeforces_669_A { public static void main ( String [ ] subhani ) throws IOException { BufferedReader br = new BufferedReader ( new InputStreamReader ( System.in ) ) ; int n = Integer.parseInt ( br.readLine ( ) ) ; solve ( n ) ; } public static void solve ( int n ) { System.out.println ( ( n / 3 ) * 2 + ( n % 3 == 0 ? 0 : 1 ) ) ; } }
