import java.io.BufferedReader ; import java.io.IOException ; import java.io.InputStreamReader ; public class codeforces_70_B { public static void main ( String [ ] args ) throws NumberFormatException , IOException { BufferedReader br = new BufferedReader ( new InputStreamReader ( System.in ) ) ; int n = Integer.parseInt ( br.readLine ( ) ) ; String input = br.readLine ( ) ; int max = - 1 ; int [ ] msgLength = new int [ input.length ( ) / 2 ] ; int count = 0 ; int idx = 0 ; for ( int i = 0 ; i < input.length ( ) ; i ++ ) { char c = input.charAt ( i ) ; if ( c == '.' || c == '?' || c == '!' ) { msgLength [ idx ++ ] = ++ count ; if ( count > max ) max = count ; i ++ ; count = 0 ; } else count ++ ; } if ( max > n ) System.out.println ( "Impossible" ) ; else { int ans = 0 ; for ( int i = 0 ; i < idx ; i ++ ) { int l = msgLength [ i ] ; while ( i < idx - 1 && l + msgLength [ i + 1 ] + 1 <= n ) { l += msgLength [ i + 1 ] + 1 ; i ++ ; } ans ++ ; } System.out.println ( ans ) ; } } }
